+const errorCodeToStatus: Record<string, number> = {
+  NOT_FOUND: 404,
+  UNAUTHORIZED: 401,
+  FORBIDDEN: 403,
+  VALIDATION: 422,
+  CONFLICT: 409,
+  RATE_LIMITED: 429,
+  INTERNAL: 500,
+};
+
+export function toHttpStatus(code: string): number {
+  return errorCodeToStatus[code] ?? 500;
+}
+
+export function toUserMessage(code: string): string {
+  const messages: Record<string, string> = { NOT_FOUND: "Resource not found", UNAUTHORIZED: "Please log in" };
+  return messages[code] ?? "An unexpected error occurred";
+}

--- a/src/middleware/pipeline.ts
+++ b/src/middleware/pipeline.ts
@@ -0,0 +1,15 @@
+type Middleware = (ctx: RequestContext, next: () => Promise<void>) => Promise<void>;
+
+export class Pipeline {
+  private middlewares: Middleware[] = [];
+
+  use(mw: Middleware): this { this.middlewares.push(mw); return this; }
+
+  async execute(ctx: RequestContext): Promise<void> {
+    let index = 0;
+    const next = async (): Promise<void> => {
+      if (index < this.middlewares.length) await this.middlewares[index++](ctx, next);
+    };
+    await next();
+  }
+}

--- a/src/db/pool.ts
+++ b/src/db/pool.ts
@@ -0,0 +1,20 @@
+import { Pool } from "pg";
+
+const pool = new Pool({
+  connectionString: process.env.DATABASE_URL,
+  max: parseInt(process.env.DB_POOL_SIZE ?? "10", 10),
+  idleTimeoutMillis: 30_000,
+  connectionTimeoutMillis: 5_000,
+});
+
+pool.on("error", (err) => {
+  console.error("Unexpected pool error:", err);
+});
+
+export async function query<T>(text: string, params?: unknown[]): Promise<T[]> {
+  const result = await pool.query(text, params);
+  return result.rows;
+}
+
+export async function drain(): Promise<void> { await pool.end(); }

